import React, { useState,useEffect} from 'react';
import Navigation from '../Navigation';
import Footer from '../Footer';

export const User = () => {
  const [userdata, setData] = useState(null);
  const [editableRows, setEditableRows] = useState({});
  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost/api/users');
      const jsonData = await response.json();
      setData(jsonData);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const closeInput = (e, id) => {
    e.preventDefault();
    setEditableRows((prevRows) => ({ ...prevRows, [id]: false }));
  };

  const updateData = (e, id) => {
    e.preventDefault();
    setEditableRows((prevRows) => ({ ...prevRows, [id]: true }));
  };

  const handleNameChange = (e, index) => {
    const updatedData = [...userdata.data];
    updatedData[index].user_name = e.target.value;
    setData({ ...userdata, data: updatedData });
  };

  const handleEmailChange = (e, index) => {
    const updatedData = [...userdata.data];
    updatedData[index].user_email = e.target.value;
    setData({ ...userdata, data: updatedData });
  };



  return (
    <>
    <Navigation />
    <div className="container">
        <div className="row">
    <table class="table table-bordered">
  <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Name</th>
      <th scope="col">Email</th>
      <th scope="col">Edit</th>
      <th scope="col">Delete</th>
    </tr>
  </thead>
  <tbody>
              {userdata &&
                userdata.data.map((item, i) => (
                  <tr key={i}>
                    <th scope="row">{i}</th>
                    <td>
                      {editableRows[i] ? (
                        <input
                          type="text"
                          value={item.user_name}
                          onChange={(e) => handleNameChange(e, i)}
                        />
                      ) : (
                        item.user_name
                      )}
                    </td>
                    <td>
                      {editableRows[i] ? (
                        <input
                          type="text"
                          value={item.user_email}
                          onChange={(e) => handleEmailChange(e, i)}
                        />
                      ) : (
                        item.user_email
                      )}
                    </td>
                    <td>
                      <button
                        type="button"
                        onClick={(e) => updateData(e, i)}
                        className="btn btn-primary"
                      >
                        Edit
                      </button>
                      <button
                        type="button"
                        onClick={(e) => closeInput(e, i)}
                        className="btn btn-primary"
                      >
                        Close
                      </button>
                    </td>
                    {/* Add your Delete button logic here */}
                  </tr>
                ))}
            </tbody>
</table>
     </div>
    </div>
    <Footer/>
    </>
  )
}
export default User;
