import React, { useState,useEffect} from 'react';
import Navigation from '../Navigation';
import Footer from '../Footer';

export const User = () => {
  const [userdata, setData] = useState(null);
  const [isEditable,setEditable]=useState(false);

  const fetchData = async () => {
    try {
      const response = await fetch('http://localhost/api/users');
      const jsonData = await response.json();
      setData(jsonData);
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  useEffect(() => {
    fetchData();
    //updateData();
  }, []);

  const closeInput=(e)=>{
    e.preventDefault();
      setEditable(false);
  }

    const updateData =(e) => {
      e.preventDefault();
      setEditable(true)

      // try {
      //   const response = await fetch('http://localhost/api/update');
      //   const jsonData = await response.json();
      //   console.log(jsonData);
      //   setData(jsonData);
      //   setEditable(true)
      // } catch (error) {
      //   console.error("Error fetching data:", error);
      // }
    };



  return (
    <>
    <Navigation />
    <div className="container">
        <div className="row">
    <table class="table table-bordered">
  <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Name</th>
      <th scope="col">Email</th>
      <th scope="col">Edit</th>
      <th scope="col">Delete</th>
    </tr>
  </thead>
  <tbody>
  {userdata ? (
        userdata.data.map((item,i) => (
          <tr>
      <th scope="row">{i}</th>
      <td>{isEditable && item.user_name?<input type="text" value={item.user_name}/>:item.user_name}</td>
      <td>{isEditable?<input type="text" value={item.user_email}/>:item.user_email}</td>
      <td>
       <button type="button" onClick={updateData} className="btn btn-primary">Edit</button>
       <button type="button" onClick={closeInput} className="btn btn-primary">Close</button>
       </td>
       <td>
       <button type="button" className="btn btn-primary">Delete</button>
      </td>
    </tr>
        ))
      ) : (
        <p>Loading...</p>
      )}
    
  </tbody>
</table>
     </div>
    </div>
    <Footer/>
    </>
  )
}
export default User;
